INSERT INTO vehicle_category (category_name, description) VALUES
('MCWOG', 'Motor Cycle Without Gear'),
('MCWG', 'Motor Cycle With Gear'),
('LMV', 'Light Motor Vehicle'),
('HPMV', 'Heavy Passenger Motor Vehicle'),
('HGMV', 'Heavy Goods Motor Vehicle');
INSERT INTO driving_exam_questions (category_id, question_text, option_1, option_2, option_3, option_4, correct_answer) VALUES
(1, 'What does a flashing red traffic light indicate?', 'Stop and proceed when safe.', 'Yield to oncoming traffic.', 'Proceed with caution.', 'Speed up.', 1),
(1, 'What is the minimum age to obtain a driving license for a motorcycle without gear (MCWOG) in India?', '16 years', '18 years', '20 years', '14 years', 1),
(1, 'What is the purpose of the helmet strap?', 'To hold sunglasses.', 'To keep the helmet securely on the head.', 'For decoration only.', 'To hang the helmet.', 2),
(1, 'Which document is NOT required to be carried while driving?', 'Driving License', 'Registration Certificate (RC)', 'Pollution Under Control (PUC) Certificate', 'Vehicle purchase invoice', 4),
(1, 'What does a solid yellow line on the road mean?', 'Overtaking is allowed.', 'No overtaking and no crossing the line.', 'You can cross the line only if no vehicle is approaching.', 'Parking is prohibited.', 2),
(1, 'Before starting a two-wheeler, what should you check first?', 'Fuel level and tires.', 'Music system.', 'Horn and lights.', 'Only the engine oil.', 1),
(1, 'What is the correct way to make a left turn on a two-wheeler?', 'Signal left, move to the right side of the road, then turn.', 'Signal left, keep to the left side of the road, and turn.', 'Signal left, stop in the middle of the road, then turn.', 'Signal left, turn sharply.', 2),
(1, 'When should you use the low beam headlights?', 'Only during the day.', 'When driving in heavy fog or rain.', 'When driving in bright sunlight.', 'Only on highways.', 2),
(1, 'What is the maximum speed limit for MCWOG in a city area (unless otherwise specified)?', '60 km/h', '40 km/h', '80 km/h', '100 km/h', 2),
(1, 'What should you do if your two-wheeler breaks down on a busy road?', 'Leave it and walk away.', 'Push it to the side of the road and display a warning sign.', 'Try to repair it in the middle of the road.', 'Call a friend to pick you up.', 2),
(1, 'When approaching a zebra crossing, what should a driver do?', 'Speed up to cross quickly.', 'Honk loudly.', 'Slow down and be prepared to stop for pedestrians.', 'Maintain constant speed.', 3),
(1, 'What is the primary function of the rear-view mirrors?', 'To check your appearance.', 'To look at passengers.', 'To see traffic behind and to the sides.', 'To adjust headlights.', 3),
(1, 'What does a blue circular sign with a white arrow pointing straight ahead mean?', 'Turn left only.', 'Go straight only.', 'No entry.', 'One way.', 2),
(1, 'Which hand signal indicates a right turn?', 'Right arm extended straight out horizontally.', 'Left arm extended straight out horizontally.', 'Left arm bent at elbow, palm down, swinging up and down.', 'Right arm bent at elbow, palm up.', 1),
(1, 'What is the safe following distance from the vehicle in front?', 'One car length.', 'Two-second rule.', 'As close as possible.', 'Five car lengths.', 2),
(1, 'What is the significance of a triangular road sign?', 'Mandatory order.', 'Information sign.', 'Cautionary or warning sign.', 'Prohibitory sign.', 3),
(1, 'When carrying a pillion rider on a two-wheeler, what is mandatory for them?', 'To wear a helmet.', 'To carry a valid driving license.', 'To have insurance.', 'To be above 18 years old.', 1),
(1, 'What does continuous honking indicate?', 'It is a sign of warning to other drivers.', 'It is allowed in silent zones.', 'It is rude and generally discouraged unless necessary.', 'It means you are in a hurry.', 3),
(1, 'What should you do before changing lanes on a two-wheeler?', 'Check mirrors, signal, and check blind spot.', 'Just signal and change lane.', 'Honk and change lane quickly.', 'Only check mirrors.', 1),
(2, 'What does the term "clutch" do on a geared motorcycle?', 'Engages the brakes.', 'Changes the speed of the engine without changing gear.', 'Disconnects the engine from the transmission.', 'Controls the suspension.', 3),
(2, 'When shifting gears on a motorcycle, what is the correct sequence?', 'Clutch in, shift gear, clutch out, accelerate.', 'Accelerate, shift gear, clutch in, clutch out.', 'Shift gear, then use the clutch.', 'Clutch in, accelerate, shift gear.', 1),
(2, 'What is the most common reason for a geared motorcycle to stall?', 'Not enough fuel.', 'Improper use of the clutch and throttle.', 'Engine is too hot.', 'Tires are flat.', 2),
(2, 'What does "downshifting" mean in a geared motorcycle?', 'Shifting to a higher gear.', 'Shifting to a lower gear.', 'Slowing down without changing gear.', 'Engaging the clutch.', 2),
(2, 'What does a circular blue sign with a red diagonal line through it indicate?', 'No parking.', 'No entry for all vehicles.', 'Speed limit.', 'Overtaking prohibited.', 4),
(2, 'What is the correct hand signal for "stopping"?', 'Right arm extended out.', 'Left arm extended straight out, palm facing down.', 'Left arm bent at the elbow, hand pointing down.', 'Right arm bent at the elbow, hand pointing up.', 3),
(2, 'What does a continuous yellow line in the center of the road mean?', 'Overtaking is allowed.', 'Overtaking is allowed with caution.', 'No overtaking and no crossing the line.', 'Overtaking is allowed only for two-wheelers.', 3),
(2, 'Where is it illegal to overtake another vehicle?', 'On a straight road.', 'When the road has a broken white line.', 'When approaching a bend or a corner.', 'On a highway.', 3),
(2, 'What should you do if an ambulance is approaching with its siren on?', 'Speed up to get out of its way.', 'Stop in the middle of the road.', 'Slow down and move to the left side of the road to give it a clear passage.', 'Start honking your horn.', 3),
(2, 'What is the primary use of the gear lever on a motorcycle?', 'To control speed and engine power.', 'To apply the rear brake.', 'To apply the front brake.', 'To shift the bike into neutral.', 1),
(2, 'In what condition should you use your horn?', 'As a warning to other drivers in a potential hazard.', 'To express impatience in traffic.', 'To greet other drivers.', 'To get the attention of pedestrians.', 1),
(2, 'What does a solid red traffic light indicate?', 'Slow down and proceed with caution.', 'Stop your vehicle behind the stop line.', 'Speed up to clear the intersection.', 'Prepare to stop.', 2),
(2, 'Which of the following is an example of an informational road sign?', 'Stop sign.', 'No U-turn sign.', 'Hospital ahead sign.', 'School zone sign.', 3),
(2, 'What is the purpose of a helmet?', 'To look fashionable.', 'To protect the rider’s head from injury in case of an accident.', 'To keep a phone in it.', 'To keep your hair tidy.', 2),
(2, 'When should you use the side stand of a motorcycle?', 'When parked on an incline.', 'When stopping briefly at a red light.', 'When you are ready to park the vehicle.', 'When riding at slow speed.', 3),
(2, 'What does a blinking red traffic light signify?', 'Stop and proceed only when it is safe to do so.', 'Stop and wait for the green light.', 'Proceed with caution without stopping.', 'Slow down and prepare to stop.', 1),
(2, 'What is the maximum number of pillion riders allowed on a two-wheeler?', 'One.', 'Two.', 'Three.', 'None.', 1),
(2, 'Where is parking a two-wheeler prohibited?', 'In a designated parking lot.', 'On a footpath.', 'In front of a private home.', 'On a bridge.', 4),
(2, 'What is the ideal position for the pillion rider on a motorcycle?', 'Sitting sideways facing the driver.', 'Standing up on the footrests.', 'Sitting astride the bike, with both hands on the rider’s waist or grab handles.', 'Clinging to the rider’s backpack.', 3),
(2, 'What does the term "counter-steering" refer to?', 'Steering in the opposite direction of the turn to initiate a turn.', 'Steering against the wind.', 'Using the handlebars to balance the bike at a stop.', 'Braking and steering simultaneously.', 1),
(3, 'What is the most important safety feature to wear while driving a car?', 'A helmet.', 'A seat belt.', 'Gloves.', 'Sunglasses.', 2),
(3, 'On a multi-lane highway, which lane should slow-moving vehicles use?', 'The leftmost lane.', 'The rightmost lane.', 'The middle lane.', 'Any lane.', 1),
(3, 'What does the term "blind spot" refer to in a car?', 'An area behind the vehicle that can be seen in the rear-view mirror.', 'An area that cannot be seen using the car''s mirrors.', 'The area directly in front of the car.', 'The area under the car.', 2),
(3, 'What is the legal speed limit for a car in a school zone?', '60 km/h', '40 km/h', '25 km/h', '80 km/h', 2),
(3, 'What should you do if your cars brakes fail while driving?', 'Shift to a lower gear, pump the brake pedal, and use the handbrake slowly.', 'Shift to a higher gear and continue driving.', 'Jump out of the moving car.', 'Turn off the engine immediately.', 1),
(3, 'Which of the following is a sign of an overheating car engine?', 'The engine light turns green.', 'A loud noise from the exhaust.', 'Steam coming from under the bonnet.', 'The fuel gauge drops rapidly.', 3),
(3, 'What does a solid white line on the road indicate?', 'Overtaking is allowed.', 'Lane changing is prohibited.', 'You can cross the line at your discretion.', 'The lane is for emergency vehicles only.', 2),
(3, 'What is the recommended safe following distance from the vehicle in front?', 'One car length.', 'Two-second rule.', 'As close as possible.', 'Five car lengths.', 2),
(3, 'When is it mandatory to use your headlights in a car?', 'Only at night.', 'During the day for better visibility.', 'At night and also during periods of low visibility like fog or heavy rain.', 'Only on highways.', 3),
(3, 'What does a "No Parking" sign indicate?', 'You can park for a short period of time.', 'You can stop but not park the vehicle.', 'Stopping and parking are both prohibited.', 'Only commercial vehicles can park here.', 3),
(3, 'What does a pedestrian crossing sign look like?', 'A triangular sign with a person walking.', 'A circular sign with a person walking.', 'A rectangular sign with a person walking.', 'A blue sign with a person walking.', 1),
(3, 'What is the correct way to merge onto a highway?', 'Stop and wait for a gap in traffic.', 'Accelerate to the speed of traffic and use your indicator to merge smoothly.', 'Accelerate as fast as possible.', 'Stay in a lower gear and merge slowly.', 2),
(3, 'What should you do when approaching a railway level crossing?', 'Speed up to cross it quickly.', 'Stop at the stop line and look for an approaching train.', 'Cross as soon as you see no vehicles.', 'Honk your horn continuously.', 2),
(3, 'What is the purpose of an airbag in a car?', 'To inflate and reduce the severity of injuries in a collision.', 'To act as a headrest.', 'To inflate and make the car lighter.', 'To make the steering wheel softer.', 1),
(3, 'What does a "Yield" sign mean?', 'Stop completely before proceeding.', 'Slow down and give way to traffic on the road you are entering.', 'Speed up to enter the flow of traffic.', 'Stop only if there is a train coming.', 2),
(3, 'Where is it illegal to make a U-turn?', 'Where there is a broken white line.', 'In front of a hospital or school.', 'At a traffic light.', 'Where there is a "No U-turn" sign.', 4),
(3, 'When can you overtake from the left side of a vehicle?', 'When the vehicle in front is signaling to turn right.', 'Whenever you want.', 'Never.', 'Only in slow-moving traffic.', 1),
(3, 'What does a red light on the dashboard that looks like an oil can mean?', 'The car is low on fuel.', 'The engine oil pressure is low.', 'The brake fluid is low.', 'The coolant level is low.', 2),
(3, 'What should you do if you miss your exit on a highway?', 'Reverse and take the exit.', 'Stop on the highway and wait for traffic to clear.', 'Proceed to the next exit and find an alternative route.', 'Make a U-turn on the highway.', 3),
(3, 'What is the purpose of a traffic roundabout?', 'To confuse drivers.', 'To allow traffic from all directions to merge smoothly without stopping.', 'To provide a shortcut for vehicles.', 'To separate traffic flows.', 2),
(4, 'What is the most crucial consideration when stopping a heavy vehicle?', 'The distance it takes to stop is the same as a car.', 'The increased stopping distance required due to its weight and momentum.', 'The brakes are more powerful and stop the vehicle instantly.', 'Heavy vehicles do not have an increased stopping distance.', 2),
(4, 'What does a sign indicating a speed limit of 40 km/h with a truck symbol below it mean?', 'The speed limit is 40 km/h for all vehicles.', 'The speed limit is 40 km/h specifically for heavy goods vehicles.', 'The speed limit is 40 km/h for heavy passenger vehicles.', 'Heavy vehicles are not allowed on this road.', 3),
(4, 'What is the primary responsibility of an HPMV driver regarding passengers?', 'To make sure they get a comfortable seat.', 'To ensure their safety, including safe boarding and deboarding.', 'To play their favorite music.', 'To provide them with food and water.', 2),
(4, 'What should a heavy vehicle driver do when approaching a railway level crossing?', 'Speed up to cross it quickly.', 'Honk continuously.', 'Stop at the designated line and proceed only after ensuring no train is approaching.', 'Slow down and wait for the train.', 3),
(4, 'What is the purpose of an engine brake on a heavy vehicle?', 'To stop the engine from running.', 'To provide extra braking power, especially on long downhill slopes.', 'To assist in acceleration.', 'To act as a parking brake.', 2),
(4, 'What is a "blind spot" for a heavy vehicle driver?', 'An area visible in all mirrors.', 'An area that cannot be seen using any of the vehicle''s mirrors.', 'The area directly behind the vehicle.', 'The area directly in front of the vehicle.', 2),
(4, 'What is the maximum permissible height for a goods vehicle in India?', '3 meters.', '3.8 meters.', '4.2 meters.', '5 meters.', 3),
(4, 'How should a heavy vehicle driver approach a sharp turn or curve?', 'Maintain high speed and brake sharply inside the turn.', 'Slow down, shift to a lower gear, and take a wide turn to avoid hitting the curb.', 'Speed up and take the turn quickly.', 'Use the handbrake to assist in turning.', 2),
(4, 'When driving a heavy vehicle, how should you navigate a steep downhill road?', 'Put the vehicle in neutral to save fuel.', 'Use a low gear and the engine brake to control speed.', 'Use the service brakes continuously and lightly.', 'Disengage the clutch and coast down.', 2),
(4, 'What is the correct procedure for a heavy vehicle when yielding to an ambulance with its siren on?', 'Continue driving until you find a safe place to stop.', 'Stop immediately in the middle of the road.', 'Move to the extreme left and slow down or stop to give it a clear passage.', 'Block the ambulance to prevent it from passing.', 3),
(4, 'What does a road sign with a circle and the number "10" with "t" written below it mean?', 'A minimum weight of 10 tons.', 'A maximum permissible weight of 10 tons.', 'The distance to a town is 10 km.', 'Parking is for vehicles weighing more than 10 tons.', 2),
(4, 'What is a tachograph used for in heavy commercial vehicles?', 'To measure fuel consumption.', 'To record the vehicle''s speed and distance traveled.', 'To monitor tire pressure.', 'To track the engine temperature.', 2),
(4, 'What is the primary danger of driving a heavy vehicle too close to the vehicle in front?', 'Reduced visibility of the road ahead and insufficient stopping distance.', 'It makes the vehicle in front go faster.', 'It saves fuel.', 'It makes the trip shorter.', 1),
(4, 'Which of the following is NOT a safety measure for a heavy vehicle driver?', 'Regularly checking tire pressure.', 'Ensuring passengers are standing while the bus is in motion.', 'Checking brakes before a journey.', 'Using mirrors frequently.', 2),
(4, 'What does the term "off-tracking" mean for a heavy vehicle?', 'The front wheels and rear wheels follow the same path during a turn.', 'The rear wheels follow a different, wider path than the front wheels during a turn.', 'The vehicle is not tracking correctly on a straight road.', 'The vehicle is going off-road.', 2),
(4, 'When driving in heavy fog, what lights should an HPMV driver use?', 'High beam headlights.', 'Low beam headlights and fog lamps.', 'No lights at all.', 'Hazard lights only.', 2),
(4, 'What is the minimum legal requirement for the driver of a heavy passenger vehicle?', 'Having a valid LMV license.', 'Having a valid Heavy Passenger Vehicle license and medical fitness certificate.', 'Having a license for a two-wheeler.', 'Having a learner''s license for a car.', 2),
(4, 'What is the significance of "bridge load limit" signs?', 'It is the maximum height of the bridge.', 'It is the maximum number of vehicles allowed on the bridge at once.', 'It indicates the maximum weight a vehicle can have to cross the bridge safely.', 'It indicates the maximum number of passengers allowed on the bridge.', 3),
(4, 'How does a heavy vehicles center of gravity affect handling?', 'A low center of gravity makes it more likely to roll over.', 'A high center of gravity makes it more stable.', 'A high center of gravity increases the risk of a rollover on turns or uneven roads.', 'The center of gravity has no effect on handling.', 3),
(4, 'What is the most common cause of accidents involving heavy vehicles?', 'Over-speeding and mechanical failure.', 'Improper turning and blind spot negligence.', 'Driving in the daytime.', 'Carrying too few passengers.', 2),
(5, 'What is the most critical factor to consider when loading a heavy goods vehicle?', 'The color of the goods.', 'Distributing the weight evenly to prevent instability and accidents.', 'Putting all the weight on one side to make it easier to turn.', 'Making sure the goods are a good fit for the truck.', 2),
(5, 'What does the term "gross vehicle weight" refer to?', 'The weight of the vehicle with a full tank of fuel.', 'The total weight of the vehicle and its maximum permissible load.', 'The empty weight of the vehicle.', 'The weight of the goods only.', 2),
(5, 'What is the safe speed to drive a heavy goods vehicle down a steep hill?', 'As fast as possible to get down quickly.', 'In a high gear to save fuel.', 'In a low gear, using engine braking to control speed and avoid brake overheating.', 'With the engine off to save fuel.', 3),
(5, 'What is the most common cause of tire blowout in a heavy goods vehicle?', 'Under-inflation or over-inflation of tires.', 'Driving too slowly.', 'Driving on a smooth road.', 'Driving in the rain.', 1),
(5, 'What does a road sign with a circle and "5" with a "t" written below it mean?', 'The minimum weight limit is 5 tons.', 'The maximum axle load is 5 tons.', 'The maximum speed limit is 5 km/h.', 'The distance to the nearest town is 5 km.', 2),
(5, 'What is the correct way to overtake another heavy vehicle on a highway?', 'Overtake from the left side at high speed.', 'Overtake from the right side after checking mirrors and signaling well in advance.', 'Tailgate the vehicle in front until it moves over.', 'Overtake from the shoulder of the road.', 2),
(5, 'What is the purpose of load securing devices on a goods vehicle?', 'To decorate the vehicle.', 'To keep the load from shifting and falling off, which could cause an accident.', 'To protect the driver from the load.', 'To help the vehicle go faster.', 2),
(5, 'When driving a heavy goods vehicle, what should you do when approaching a roundabout?', 'Speed up to enter the roundabout first.', 'Yield to traffic already in the roundabout and enter when it is safe.', 'Stop completely and wait for an opening.', 'Always drive on the right side of the roundabout.', 2),
(5, 'What is the primary danger of driving a heavily loaded vehicle?', 'It can become too light to handle.', 'Its increased braking distance and reduced acceleration.', 'It is more fuel-efficient.', 'It becomes easier to turn.', 2),
(5, 'What does a red light on a dashboard that looks like a temperature gauge mean?', 'The engine is too cold.', 'The engine is overheating and requires immediate attention.', 'The engine is at an optimal temperature.', 'The air conditioning is on.', 2),
(5, 'What is the purpose of the "clean air sticker" on a commercial vehicle?', 'It indicates that the vehicle has a new engine.', 'It shows that the vehicle complies with emission standards.', 'It means the vehicle is clean.', 'It is used for decoration.', 2),
(5, 'What should a heavy goods driver do during a long journey to prevent fatigue?', 'Drive continuously without any breaks.', 'Stop frequently for rest, a nap, and a meal.', 'Drink caffeinated beverages and continue driving.', 'Only stop for fuel.', 2),
(5, 'What is the legal limit for a driver''s blood alcohol content (BAC) while driving a commercial vehicle in India?', '0.03% (30 mg per 100 ml of blood)', '0.08%', '0.12%', 'No limit.', 1),
(5, 'What does a road sign showing a truck and a car with an arrow in between them pointing right mean?', 'Heavy goods vehicles must overtake from the right.', 'Overtaking by heavy goods vehicles is prohibited.', 'Trucks are allowed in the right lane only.', 'Trucks must stay behind cars.', 2),
(5, 'What is the purpose of the fifth wheel coupling on a semi-trailer truck?', 'To secure the fuel tank.', 'To connect the tractor unit to the trailer.', 'To provide an extra wheel for stability.', 'To hold the spare tire.', 2),
(5, 'What is the primary reason for a heavy goods vehicle to take a wide turn?', 'To confuse other drivers.', 'To avoid hitting the curb with the rear wheels, which follow a shorter path.', 'To show off.', 'To intimidate other vehicles.', 2),
(5, 'What does a sign showing a red circle with a vehicle and the word "No Entry" mean?', 'Entry is prohibited for all vehicles.', 'Entry is prohibited for heavy goods vehicles only.', 'Entry is prohibited for cars only.', 'Parking is prohibited for all vehicles.', 1),
(5, 'What is the safest way to manage your speed while descending a long hill?', 'Downshift and use the engine brake to control speed.', 'Use the service brakes continuously.', 'Put the vehicle in neutral.', 'Accelerate to get down faster.', 1),
(5, 'What is the maximum width of a motor vehicle in India?', '1.5 meters.', '2.5 meters.', '3.5 meters.', '4.0 meters.', 2),
(5, 'What is the most important thing to check on a heavy vehicle before every journey?', 'The music system.', 'Lights, brakes, tires, and load security.', 'The number of passengers.', 'The air conditioning.', 2);
INSERT INTO users (username, password_hash, role, name, dob, address, email, phone, state) VALUES
('VikramSinghRTO', '321drowssap#123', 'Officer', 'Vikram Singh', '1985-07-22', 'RTO Zonal Office, Subhash Bridge, Ahmedabad', 'vikram.singh.rto@gov.in', '9898012345', 'Gujarat');